CREATE TABLE `news` (
   `id` int(11) not null auto_increment,
   `username` varchar(250) not null,
   `headline` text not null,
   `news` text not null,
   `imgName` varchar(250) not null,
   `date` varchar(250) not null,
   PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=13;

